/**
 * Enum for the different types of email distribution supported by Jarvis.
 * VERY IMPORTANT these do not change as portal preferences are stored against these values
 */
export enum EmailDistributionType {
  all = 'all',
  dailyUpdate = 'dailyUpdate',
  dps = 'dps',
  fysa = 'fysa',
  keyContact = 'keyContact',
  monthlySector = 'monthlySector',
  portal = 'portal',
  proactiveAlerts = 'proactiveAlerts',
  weeklyOutlook = 'weeklyOutlook',
}
