import { EmailDistributionType } from './enums/email-distribution-type';

/**
 * Checks if a specific email preference is enabled in the portal preferences.
 * @param portalPreferences - The portal preferences object.
 * @param preference - The email distribution type to check.
 * @returns True if the preference is enabled, otherwise false.
 */
export function checkEmailPreferences(
  portalPreferences: any,
  preference: EmailDistributionType,
): boolean {
  const emailPreferences = portalPreferences?.preferences?.['email'];
  if (typeof emailPreferences !== 'object' || emailPreferences === null)
    return false;
  return emailPreferences[preference] !== undefined;
}
