import { z } from 'zod';

export interface PublishedPostMessage {
  actionId?: string;
  articleId?: string;
}

// Define the Zod schema for PublishedPostMessage
export const PublishedPostMessageSchema = z.object({
  actionId: z.string().optional(),
  articleId: z.string().optional(),
});

// Infer TypeScript type from the schema (optional, but useful)
export type PublishedPostMessageType = z.infer<
  typeof PublishedPostMessageSchema
>;
